import React, { Fragment, useEffect, useState } from 'react';
import { Redirect } from 'react-router-dom';
import anime from 'animejs';

import { WELCOME, EXPERIENCE } from '../constants/routes';

const styles = {
  pageContainer: {
    height: '100vh',
    width: '100vw',
    background: '#2e3440'
  },
  cardsContainer: {
    display: 'flex',
    flexDirection: 'row'
  },
  card: {
    flexGrow: 1,
    background: '#eceff4',
    borderRadius: 10,
    margin: 25,
    marginTop: '10vh',
    height: '75vh',
    boxShadow: '0 5px 10px black'
  }
};

const ProjectCards = () => {
  const [toRedirect, setRedirect] = useState(false);

  useEffect(() => {
    openCardsAnimation();
  }, []);

  const openCardsAnimation = () => {
    anime({
      targets: '.projectCard',
      scaleY: [0, 1]
    });
  };

  let toRender;
  if (toRedirect) {
    toRender = <Redirect to={EXPERIENCE} />;
  } else {
    toRender = (
      <Fragment>
        <div style={styles.pageContainer}>
          <div style={styles.cardsContainer} className="cardsContainer">
            <div style={styles.card} className="projectCard">
              {/* <Link to={WELCOME}>Back</Link> */}
              {/* <Link to={EXPERIENCE}>Next</Link> */}
            </div>
            <div style={styles.card} className="projectCard" />
            <div style={styles.card} className="projectCard" />
          </div>
        </div>
      </Fragment>
    );
  }

  return toRender;
};

export default ProjectCards;
