import React, { Fragment, useEffect, useState } from 'react';
import { Redirect } from 'react-router-dom';
import anime from 'animejs';

import { WELCOME, EXPERIENCE } from '../constants/routes';

const styles = {
  pageContainer: {
    height: '100vh',
    width: '100vw',
    background: '#2e3440'
  },
  cardsContainer: {
    display: 'flex',
    flexDirection: 'row'
  },
  card: {
    flexGrow: 1,
    background: '#eceff4',
    borderRadius: 10,
    margin: 25,
    marginTop: '10vh',
    height: '75vh',
    boxShadow: '0 5px 10px black'
  }
};

const ProjectCards = () => {
  const [toRedirect, setRedirect] = useState({
    destination: ''
  });

  const { destination } = toRedirect;

  useEffect(() => {
    cardsAnimation(true);
  }, []);

  const cardsAnimation = open => {
    const animationDirection = open ? 'normal' : 'reverse';

    anime({
      targets: '.projectCard',
      scaleY: [0, 1],
      direction: animationDirection,
      duration: 750
    });
  };
  const delay = ms => new Promise(res => setTimeout(res, ms));

  const handleClick = async stateVal => {
    cardsAnimation(false);
    await delay(1000);
    setRedirect({ destination: stateVal });
  };

  let toRender;
  if (destination != '') {
    toRender = <Redirect to={destination} />;
  } else {
    toRender = (
      <Fragment>
        <div style={styles.pageContainer}>
          <div style={styles.cardsContainer} className="cardsContainer">
            <div style={styles.card} className="projectCard">
              <g
                xmlns="http://www.w3.org/2000/svg"
                id="Layer_3"
                data-name="Layer 3"
              >
                <rect
                  class="cls-4"
                  x="1030"
                  width="495"
                  height="802"
                  rx="27.84"
                />
                <path
                  class="cls-2"
                  d="M1483.55,49a9.66,9.66,0,0,1,9.65,9.65v684.7a9.66,9.66,0,0,1-9.65,9.65H1072.16a9.66,9.66,0,0,1-9.65-9.65V58.65a9.66,9.66,0,0,1,9.65-9.65h411.39m0-5H1072.16a14.65,14.65,0,0,0-14.65,14.65v684.7A14.65,14.65,0,0,0,1072.16,758h411.39a14.65,14.65,0,0,0,14.65-14.65V58.65A14.65,14.65,0,0,0,1483.55,44Z"
                />
                <path
                  class="cls-2"
                  d="M1369.17,49a123.68,123.68,0,0,1,123.67,123.68V629.32A123.68,123.68,0,0,1,1369.17,753H1185.83a123.68,123.68,0,0,1-123.67-123.68V172.68A123.68,123.68,0,0,1,1185.83,49h183.34m0-5H1185.83a128.68,128.68,0,0,0-128.67,128.68V629.32A128.68,128.68,0,0,0,1185.83,758h183.34a128.68,128.68,0,0,0,128.67-128.68V172.68A128.68,128.68,0,0,0,1369.17,44Z"
                />
                <rect
                  class="cls-2"
                  x="1196.75"
                  y="495"
                  width="186"
                  height="32"
                />
                <rect
                  class="cls-2"
                  x="1221.25"
                  y="464"
                  width="137"
                  height="30"
                />
                <rect
                  class="cls-2"
                  x="1282.88"
                  y="230"
                  width="13.75"
                  height="234"
                />
                <rect
                  class="cls-2"
                  x="1157.25"
                  y="296"
                  width="261"
                  height="9"
                  transform="translate(200.85 -452.66) rotate(21.58)"
                />
                <path class="cls-5" d="M1159,356" />
                <path
                  class="cls-6"
                  d="M1240,356c-1.26-53.86-26.48-90-45-90s-43.74,36.14-45,90"
                />
                <rect class="cls-2" x="1132" y="356" width="126" height="9" />
                <path
                  class="cls-6"
                  d="M1420,428c-1.26-53.86-26.48-90-45-90s-43.74,36.14-45,90"
                />
                <rect class="cls-2" x="1312" y="428" width="126" height="9" />
              </g>
              <button onClick={() => handleClick('/')}>Back</button>
              <button onClick={() => handleClick('/experience')}>Next</button>
            </div>
            <div style={styles.card} className="projectCard" />
            <div style={styles.card} className="projectCard" />
          </div>
        </div>
      </Fragment>
    );
  }

  return toRender;
};

export default ProjectCards;
